#  Let's keep working with the same code as the previous question:

class CreditCard:
    def __init__(self, balance, interest_rate):
        self.balance = balance
        self.interest_rate = interest_rate
    def calculate_interest(self):
        monthly_interest_rate = self.interest_rate / 12 
        monthly_interest = self.balance * monthly_interest_rate 
        return monthly_interest
    def calculate_total_payment(self):
        monthly_interest = self.calculate_interest() 
        total_payment = self.balance + monthly_interest 
        return total_payment
class Card(CreditCard):
    def __init__(self, card_number, card_holder, balance, interest_rate):
        super().__init__(balance, interest_rate) 
        self.card_number = card_number 
        self.card_holder = card_holder

# New code for this question:

class RewardsCard(CreditCard):
    def __init__(self, credit_card, credit_score):
        super().__init__(credit_card.balance, credit_card.interest_rate) 
        self.credit_score = credit_score
        self.adjusted_interest_rate = credit_card.interest_rate
    def calculate_interest(self):
        if self.credit_score > 700:
            adjusted_interest_rate = self.adjusted_interest_rate - 2.0
            return adjusted_interest_rate
        else:
            return super().calculate_interest()
